import React from 'react'

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
var list=document.getElementById('item');

  function add(e)
  {
    e.preventDefault();




      var newItem=document.getElementById('item-name').value;
      var newItem1=document.getElementById('item-unit').value;
      var tot=document.getElementById("item-total").value*newItem1;


      localStorage.setItem("newItem",newItem);
      localStorage.setItem("newItem1",newItem1);
      localStorage.setItem("tot",tot);


    var l=document.createElement('tr');
    var li=document.createElement('td')
    var li1=document.createElement('td')
    var li2=document.createElement('td')
    var li3=document.createElement('button')
    var li4=document.createElement('button')
    li.id="items"
    li1.id="items"
    li2.id="items"

    li.contentEditable="true";
    li1.contentEditable="true";
    li2.contentEditable="true";



    l.id="myP";
    l.contentEditable="true";

    li3.className="fas";
    li4.className="fas";


    li.appendChild(document.createTextNode(newItem));  
    li1.appendChild(document.createTextNode(newItem1));
    li2.appendChild(document.createTextNode(tot));

    li3.appendChild(document.createTextNode("edit"));


   l.appendChild(li)
   l.appendChild(li1)
   l.appendChild(li2)
   l.appendChild(li3)
   l.appendChild(li4)
   list.appendChild(l);
   totall();
  }

  function totall()
  {

      var u=document.getElementById("tot_unit");
      var a=document.getElementById("tot_amount");
      var table=document.getElementById("item"),sumPrice=0,sumUnit=0; 

      for(var i=1;i<table.rows.length;i++)
      {
          sumPrice=sumPrice+parseInt(table.rows[i].cells[2].innerHTML);
          sumUnit=sumUnit+parseInt(table.rows[i].cells[1].innerHTML);
      }
    u.value=sumUnit;
    a.value=sumPrice;
  }




export default function grocery() {
    return (
        <>

        <div class="container">
        <div class="grocery">
        <h4 >Grocery Shopping<FontAwesomeIcon icon={['fas', 'fa-shopping-basket']} /></h4>
            <h5>Add Item</h5>

      <form class="form" id="formadd">

              Item name <input type="text" class="form-item"  mr-2 id="item-name" />

              units <input type="text" class="form-item" mr-2 id="item-unit" />
              Price <input type="text" class="form-item" mr-2 id="item-total" />
             <button onClick={add}>Submit</button>
                      </form>
                      <table  id="item">
  <tr>
  <th> Item</th>
  <th>Unit</th>
  <th>Total</th>
  <th> Edit</th>
  </tr>

  </table>
  <table id="item" >
    <tr>

    <th> Total units
      <input class="form-item1" name="tot_amount" id="tot_unit" type="text" readonly/></th>
    <th >Total Price <input class="form-item1" name="tot_amount" id="tot_amount" type="text" readonly /></th>

    </tr>

    </table>
        </div>
      </div>
      <script src="https://kit.fontawesome.com/a6f1ed60fd.js" crossorigin="anonymous"></script>
    <script src="https://unpkg.com/uuid@latest/dist/umd/uuidv4.min.js"></script>
    </>

    )
}
