import React,{useState} from "react";
import image from "./image.png";

export const Grocery = () => {
  const [inputData,setInputData]=useState('');
const [items,setItems]=useState([]);


  const addItem =() =>{

   setItems([...items,inputData])
  setInputData('');
  }

    return (
    <>
      <div className="main-div">
        <div className="child-div">
          <figure>
            <img src={image} alt="shopping images" />
            <figcaption> Grocery Shopping </figcaption>
          </figure>
          <div className="addItems">
            <input type="text" placeholder="enter item name" value={inputData} onChange={(e)=> setInputData(e.target.value)}/>
            <i className="fa fa-plus add-btn" title="Add Item" onClick={addItem}></i>
          </div>
          <div className="showItems">
            <div className="eachItem">
              <h3> Apple</h3>
              <i className="far fa-trash-alt add-btn" title="Delete item"></i>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
