    <form class="form" id="formadd">
      <legend>
        Grocery Shopping
      </legend>
            Item name <input type="text" class="form-item"  mr-2 id="item-name" />

            units <input type="text" class="form-item" mr-2 id="item-unit" />
            Price <input type="text" class="form-item" mr-2 id="item-total" />
           <input type="submit" class="btn btn-dark" value="submit" />
                    </form>

    </>
  )
}

